<?phpclass SessionsController extends \BaseController {	/**	 * Display a listing of the resource.	 * GET /sessions	 *	 * @return Response	 */	public function index()	{		return View::make('sessions.index');	}	/**	 * Show the form for creating a new resource.	 * GET /sessions/create	 *	 * @return Response	 */	public function create()	{		return View::make('sessions.create');	}	/**	 * Store a newly created resource in storage.	 * POST /sessions	 *	 * @return Response	 */	public function store(){	$input = Input::all();	$attempt = Auth::attempt([		'email' => $input['email'],		'password' => $input['password']	]);	if ($attempt) return Redirect::intended('/')->with('flash_message', 'You have been logged in.');	return Redirect::back()->with('flash_message', 'Invalid credentials.')->withInput();}	public function destroy($id = null)	{		Auth::logout($id);		return Redirect::home()->with('flash_message','You have been logged out.');	}}